White list :  [Bind(Include="UserName,Location")]
or black list: [Bind(Exclude="Password")]
// POST: User/Create
[HttpPost]
public ActionResult Create([Bind(Include="UserName,Location")]User user)
{    
   try{
      _db.Users.Add(user);
      _db.SaveChanges();
      return RedirectToAction("Index");
   }catch{
      return View();
   }
}

// POST: User/Edit
[HttpPost]
public ActionResult Edit([Bind(Exclude="Password,UserId")]User user) 
{     
   try{
      _db.Users.Update(user);
      _db.SaveChanges();
      return RedirectToAction("Index");
   }catch{
      return View();
   }
}

